inline int get (int r) {
	int x = r / block;
	int res = s[x];
	int F = f[x];

	for (int i = x*block + 1; i <= r; ++i) {
		F = F * 1ll * i % mod;
		res += F;
		if (res >= mod)
			res -= mod;
	}

	return res;
}

inline int get (int l, int r) {
	int res = get(r) - (!l ? 0 : get(l - 1));
	if (res < 0)
		res += mod;
	return res;
}
