int n, x[N], y[N], sx[N], sy[N], ans;
bool ok[N];
vpii g[N];
vi vx, vy;

main () {
	cin >> n;
	for (int i = 0; i < n; ++i)
		cin >> x[i] >> y[i];

	if (x[0] == x[1]) {
		for (int i = 0; i < n; ++i)
			swap(x[i], y[i]);
	}

	for (int i = 0; i < n; ++i) {
		vx.pb(x[i]);             
		vy.pb(y[i]);
	}

	sort(all(vx));
	sort(all(vy));

	vx.resize(unique(all(vx)) - vx.begin());
	vy.resize(unique(all(vy)) - vy.begin());

	for (int i = 0; i < n; ++i) { 
		x[i] = lower_bound(all(vx), x[i]) - vx.begin();
		y[i] = lower_bound(all(vy), y[i]) - vy.begin();
	}

	for (int i = 0; i < sz(vx) - 1; ++i)
		sx[i] = get(vx[i], vx[i + 1] - 1);

	for (int i = 0; i < sz(vy) - 1; ++i)
		sy[i] = get(vy[i], vy[i + 1] - 1);


	for (int i = 0; i < n; i += 2)  {
	    if (x[i] > x[i + 1])
	    	swap(x[i], x[i + 1]);

		g[y[i]].pb({x[i], x[i + 1]});
	}

	
	for (int i = 0; i < n; ++i) {
		int s = 0;
		for (int j = 0; j + 1 < sz(vx); ++j) {
			s += ok[j] * sx[j];
			if (s >= mod)
				s -= mod;
		}
		
		if (i) 
			ans = (ans + s * 1ll * sy[i - 1]) % mod;

//		sort(all(g[i]));
		for (auto j : g[i]) {
//		    cerr << "{" << j.f << ", " << j.s << "} ";
			for (int x = j.f; x < j.s; ++x) 
				ok[x] ^= 1;  
		}                
//		cerr << endl;

	}

	cout << ans << endl;



}